import React , {createContext,useMemo} from "react";
import {io} from "socket.io-client";


const SocketContext=createContext(null);
export const useSocket=()=>{
    const socket=useContext(SocketContext);
    return socket;
}


export const SocketProvider=(props)=>{
    const socket=useMemo(()=>io('localhost:8000'))// I doesn't need to initialised it again and again
    // here i have created a socket and pass it in value so, i can use this everywhere in the component


    // creating the context here

  


    return(
        <SocketContext.Provider value={socket}>

            {props.childern}
        </SocketContext.Provider>
    )
}
