import styled, { css } from 'styled-components'

  const TopNavWrapper = styled.header`
    position: absolute;
    top: 0;
    left: 0;
    min-height: var(--topnav-height);
    width: 100%;
    padding: 2rem 5%;
    background: var(--neutral-dk);
    z-index: 1000;
  
    a {
      color: var(--neutral-lt);
      font-size: 1.4rem;
    }
    a:hover {
      color: var(--primary-lt);
    }
    nav {
      display: flex;
      align-items: center;
      justify-content: space-between;
      gap: 2rem;
      width: 100%;
    }
    .logo {
      font-family: var(--font-display);
      font-size: 2rem;
      line-height: 0.9;
    }
    .nav-links {
      display: flex;
      gap: 2rem;
      font-size: 1.4rem;
    }
    @media screen and (min-width: 750px) {
      .logo {
        font-size: 2.6rem;
      }
    }
  `;
  
  const TopNav = () => {
    return (
      <TopNavWrapper id="top">
        <nav>
          <a href="#top" className="logo">
            Dog Lover Biz
          </a>
